import React, {useState, useEffect} from "react";

import { useLocation } from 'react-router-dom';

import {  
    IonLabel, 
    IonButton,  
    IonCol, 
    IonList,
    IonItem,
    IonAvatar
  } from '@ionic/react';

import '../assets/css/Custom.css';
import '../assets/css/Responsive.css';

const TENANTS = "tenants"
const RENTS = "rents"
const REPAIRS = "repairs"
const POSTS = "posts"
const STAFF = "staff"


const DashboardSidebar: React.FC = () => {

    const location = useLocation();

    const [userType, setUserType] = useState<string>();
    let element: HTMLElement = document.getElementById('first') as HTMLElement;

    // const addActiveClass = async (event: any) => {
    //     console.log(event.detail.value);
    //     // setUserType(event?.detail?.id);
    // }

    const [activeButton, setActiveButton] = useState('');

    const addActiveClass = (e: any) => {
        console.log(e.target);
        const { name } = e.target;
        setActiveButton(name);
        console.log(activeButton);
    };


    switch(userType) {
        case TENANTS: {
            let element: HTMLElement = document.getElementById(TENANTS) as HTMLElement;
            element.classList.add('active');
            break;
        }
        case STAFF: {
            let element: HTMLElement = document.getElementById(STAFF) as HTMLElement;
            element.classList.add('active');
            break;
        }
        default: {
            break;
        }
           
    }
    

    // useEffect(() => {
    //     let element: HTMLElement = document.getElementById('first') as HTMLElement;
    //     console.log(element);

    //     if (location.pathname === '/manage-tenants') {
        
    //     element.classList.add('active')
    //     } else {
    //         element.classList.remove('active')

    //     }
    // }, [location.pathname])

    return (

        <IonCol className="dashboard-sidebar" size="12" sizeMd="12" sizeLg="3" sizeXl="2">
            <p className="ion-hide-lg-up">What would you like to do today?</p>

            <IonList className="sidebar-menu-list">
                <IonItem>
                    <IonButton id={TENANTS} className="dashboard-button" fill="clear" routerLink="/manage-tenants" onClick={addActiveClass}>
                        <div className="dashboard-button-inner">
                            <IonAvatar>
                                <img src="assets/images/Manage-Lease-home.svg" />
                            </IonAvatar>
                            <IonLabel  className="dashboard-button-label"><b>Tenants</b> & <b>Leases</b></IonLabel>
                        </div>
                    </IonButton>
                </IonItem>

                <IonItem>
                    <IonButton id={RENTS} className="dashboard-button" fill="clear" routerLink="/manage-rents" onClick={addActiveClass}>
                        <div className="dashboard-button-inner">    
                            <IonAvatar>
                                <img src="assets/images/Pay-Rent-home.svg" />
                            </IonAvatar>
                            <IonLabel  className="dashboard-button-label"><b>Rents</b></IonLabel>
                        </div>
                    </IonButton>
                </IonItem>

                <IonItem>
                    <IonButton className="dashboard-button" fill="clear" routerLink="#">
                        <div className="dashboard-button-inner">
                            <IonAvatar>
                                <img src="assets/images/Repair-home.svg" />
                            </IonAvatar>
                            <IonLabel  className="dashboard-button-label"><b>Repairs</b> & <b>Requests</b></IonLabel>
                        </div>
                    </IonButton>
                </IonItem>

                <IonItem>
                    <IonButton className="dashboard-button" fill="clear" routerLink="#">
                        <div className="dashboard-button-inner">
                            <IonAvatar>
                                <img src="assets/images/Posts-Home-Icon.svg" />
                            </IonAvatar>
                            <IonLabel  className="dashboard-button-label"><b>Posts</b> & <b>Marketplace</b></IonLabel>
                        </div>
                    </IonButton>
                </IonItem>

                <IonItem>
                    <IonButton className="dashboard-button" slot="start" fill="clear" routerLink="#">
                        <div className="dashboard-button-inner">
                            <IonAvatar>
                                <img src="assets/images/Staff-home.svg" />
                            </IonAvatar>
                            <IonLabel  className="dashboard-button-label"><b>Staff</b> accesses</IonLabel>
                        </div>
                    </IonButton>
                </IonItem>
            </IonList>
        </IonCol>
        
    );
};

export default DashboardSidebar;
