import React, { useEffect, useRef, useState } from 'react';

import { 
   IonContent, 
   IonPage, 
   IonGrid, 
   IonRow,
   IonCol, 
   IonLabel, 
   IonInput, 
   IonButton, 
    IonSlides, 
    IonSlide, 
} from '@ionic/react';
import {  useForm, Controller } from "react-hook-form";

import Header from '../components/Header';

import 'swiper/swiper-bundle.min.css';
import '@ionic/react/css/ionic-swiper.css';
import '../assets/css/Custom.css';
import '../assets/css/Responsive.css';

const slideOpts = {
   initialSlide: 0,
   speed: 400
};

const ManagementSignup: React.FC = () => {
   const mySlides = useRef<any>(null);

   const { register, trigger, handleSubmit, formState: { errors } } = useForm();
   console.log(errors);

   useEffect(() => {
      mySlides.current.lockSwipes(true);
   });

   const next = async (fields: any) => {
      const result = await trigger(fields);
      if (!result) return;
      await mySlides.current.lockSwipes(false);
      await mySlides.current.slideNext();
      await mySlides.current.lockSwipes(true);
   };
   const prev = async () => {
      await mySlides.current.lockSwipes(false);
      await mySlides.current.slidePrev();
      await mySlides.current.lockSwipes(true);
    };
  
    
   const onSubmit = (data: any) => {
      console.log(data);
   };


   return (
      <IonPage>

         <Header />

         <IonContent fullscreen>
            <IonGrid>
               <IonRow className="signup-form login-form-row">
                  <IonCol size="10" sizeMd="6" sizeLg="4">
                     <form onSubmit={handleSubmit(onSubmit)}>
                        <IonSlides pager={true} options={slideOpts} ref={mySlides}>
                           <IonSlide>
                                 <IonGrid>
                                    <IonRow>
                                       <IonCol size="12" className="email-field">
                                             <IonLabel className="form-lable">Email*</IonLabel>
                                             {/* <IonInput 
                                                name="email" 
                                                type="email" 
                                                {...register("firstname", { required: true, maxLength: 30 })} 
                                             ></IonInput> */}
                                             <IonInput
                                                {...register('email', {
                                                   required: 'Email is a required field',
                                                   pattern: {
                                                   value: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i,
                                                   message: 'invalid email address'
                                                   }
                                                })}
                                             />
                                             {errors.email && <p>This is required.</p>}
                                       </IonCol>

                                       <IonCol size="12" className="password-field">
                                             <IonLabel className="form-lable">Password*</IonLabel>
                                             <IonInput 
                                                name="password-1" 
                                                type="password" 
                                                // ref={register}
                                             ></IonInput>
                                       </IonCol>

                                       <IonCol size="12" className="forget-password">
                                             <IonLabel className="form-lable">Confirm Password*</IonLabel>
                                             <IonInput 
                                                name="password-2" 
                                                type="password" 
                                                // ref={register}
                                             ></IonInput>
                                       </IonCol>

                                       <IonCol size="12" className="sign-up-btn">
                                             <IonButton expand="block" shape="round" fill="outline" onClick={() => next(['email'])}>
                                                Continue
                                             </IonButton>
                                       </IonCol>

                                    </IonRow>
                                 </IonGrid>
                           </IonSlide>

                           <IonSlide>
                                 <IonGrid>
                                    <IonRow>

                                       <IonCol size="12" className="email-field">
                                             <IonLabel className="form-lable">First Name:</IonLabel>
                                             <IonInput 
                                                name="first" 
                                                type="text"
                                                // ref={register}
                                             ></IonInput>
                                       </IonCol>

                                       <IonCol size="12" className="email-field">
                                             <IonLabel className="form-lable">Last Name:</IonLabel>
                                             <IonInput 
                                                name="last" 
                                                type="text"
                                                // ref={register}
                                             ></IonInput>
                                       </IonCol>

                                       <IonCol size="12" className="email-field">
                                             <IonLabel className="form-lable">Telephone:</IonLabel>
                                             <IonInput 
                                                name="telephone" 
                                                type="number"
                                                // ref={register}
                                             ></IonInput>
                                       </IonCol>

                                       <IonCol size="12" className="sign-up-btn">
                                             <IonButton expand="block" shape="round" fill="outline" onClick={() => prev()}>
                                                previous
                                             </IonButton>
                                       </IonCol>

                                       <IonCol size="12" className="sign-up-btn">
                                             <IonButton type="submit" expand="block" shape="round" fill="outline">
                                                Finalize
                                             </IonButton>
                                       </IonCol>

                                    </IonRow>
                                 </IonGrid>
                           </IonSlide>

                        </IonSlides>
                     </form>
               </IonCol>

               </IonRow>
            </IonGrid>
         </IonContent>

      </IonPage>
   );
};

export default ManagementSignup;
