import React, {useRef, useState, useEffect} from "react";

import { 
    IonContent, 
    IonPage, 
    IonGrid, 
    IonRow, 
    IonCol, 
    IonAlert
} from '@ionic/react';

import { addSharp, close } from "ionicons/icons";

import HeaderMain from '../components/Header-main';
import Footer from '../components/Footer';
import FooterMobile from '../components/Footer-mobile';
import DashboardSidebar from '../components/Dahsboard-sidebar';

import '../assets/css/Custom.css';
import '../assets/css/Responsive.css';
import { Routes } from '../App';
  
const Chat: React.FC<{ path: string }> = ({path}) => {

    const [call, setCall] = useState(false);

    return (
        <IonPage>
  
           <HeaderMain pageTitle="Messaging" logoHide="hide-logo" />
  
           <IonContent className="dashboard-wrapper manage-tenants-wrapper manage-rents-wrapper" fullscreen>
                <IonGrid className="dashboard-main-grid">
                    <IonRow className="dashboard-main-row">
                        
                        {/* sidebar start  */}
                        <DashboardSidebar path={path} />
                        {/* sidebar end  */}


                        {/* dashboar content start */}
                        <IonCol className="dashboard-content messaging-content" size="12" sizeMd="12" sizeLg="6" sizeXl="8">
                            <IonRow className="chat-header">
                                <IonCol className="sender-details" size="4" sizeMd="4" sizeLg="4" sizeXl="3">
                                    <h6>Messaging With</h6>
                                    <h5><b>Julie.J</b></h5>
                                    <p>(Management)</p>
                                </IonCol>
                                <IonCol className="chat-time ion-text-right" size="8" sizeMd="8" sizeLg="8" sizeXl="9">
                                    <h6>Office hours: <b>Mon-Fri, 10am-5pm</b></h6>
                                    <h6>Call Front Desk: <a className="sender-number" onClick={() => setCall(true)}>514-XXX-XXXX</a></h6>
                                </IonCol>
                                <IonAlert
                                    isOpen={call}
                                    onDidDismiss={() => setCall(false)}
                                    cssClass='orange-alert'
                                    mode='md'
                                    header={'Call Staff'}
                                    message={'<p>Do you want to call staff, with your phone number?</p>'}
                                    buttons={[
                                        {
                                            text: 'Yes',
                                            cssClass: 'btn-secondary',
                                            handler: () => {
                                                console.log('Exit File Okay');
                                            }
                                        },
                                        {
                                            text: 'No',
                                            role: 'cancel',
                                            cssClass: 'btn-outline',
                                            handler: () => {
                                                console.log('Exit File Cancel');
                                            }
                                        }
                                        
                                    ]}
                                />
                            </IonRow>

                        </IonCol>
                        {/* dashboar content end */}


                        {/* dashboar posts start */}
                        <IonCol  className="dashboard-posts" size="12" sizeMd="12" sizeLg="3" sizeXl="2">
                            
                        </IonCol>
                        {/* dashboar post end */}
    
                    </IonRow>
                </IonGrid>
              
                <Footer />

            </IonContent>

            <FooterMobile />
      </IonPage>
    );
  };
  
  export default Chat;
