import React, {useState, useEffect} from 'react';

import { 
    IonGrid,
    IonRow,
    IonCol,
    IonButton,
    IonIcon,
    IonInput,
    IonLabel,
    IonSearchbar,
    IonCard,
    IonCardContent,
    IonCardHeader,
    IonCardTitle,
    IonCardSubtitle,
    IonFooter
} from '@ionic/react';

import '../assets/css/Custom.css';
import '../assets/css/Responsive.css';

export const SEARCH = [
    {
      id: "s1",
      title: "Business",
      detail:
        "It is a long established fact that a reader will be distracted by the readable content of a page when looking at its layout. The point of using Lorem Ipsum is that it has a more-or-less normal distribution of letters, as opposed to using 'Content here, content here', making it look like readable English.",
      page: "/search-business",
    }
  ];
  

const StartConversationPopup: React.FC = () => {

    const [searchText, setSearchText] = useState('');
    const [searchQuery, setSearchQuery] = useState('');
    const [filteredSearch, setFilteredSearch] = useState([
    {
      id: "",
      title: "",
      detail: "",
      page: "",
    }])
  
    useEffect(() => {
        let tempSearchResult = SEARCH.filter(ele => ele.title.includes(searchQuery))
        setFilteredSearch([...tempSearchResult])
    },[searchQuery])

    return (

        <IonGrid className="start-conversation-popup-wrapper">

            <div className="popup-title">
                <h3>Start a <span>Conversation</span></h3>
            </div> 

            <div className="popup-content">

                <div className="search-box">
                    <IonSearchbar  mode="ios" placeholder="Search Apt # or Contact name" value={searchQuery} onIonChange={e => setSearchQuery(e.detail.value!)}></IonSearchbar>
                </div>
                <div className="search-results">
                    <p>Search results</p>
                    <IonRow className="search-results-header">
                        <IonCol size="4">Name</IonCol>
                        <IonCol size="4">Apt #</IonCol>
                        <IonCol size="4">Role</IonCol>
                    </IonRow>
                    {filteredSearch.map((search) => (
                        <IonRow key={search.id} className="search-results-rows">
                            <IonCol size="4">{search.title}</IonCol> 
                            <IonCol size="4">{search.detail}</IonCol> 
                            <IonCol size="4">{search.page}</IonCol> 
                        </IonRow>
                    ))}
              

                </div>
                <IonRow  className="popup-buttons">
                    <IonCol size="6">
                        <IonButton className="exit-button" fill="outline" shape="round">
                            Exit
                        </IonButton>
                    </IonCol>
                    <IonCol size="6">
                        <IonButton className="secondary-button" fill="outline" shape="round">
                            Confirm
                        </IonButton>
                    </IonCol>
                </IonRow>
            </div>

        </IonGrid> 
        // </IonPopover>
        
    );
};

export default StartConversationPopup;
