import React, {useState} from "react";

import { useHistory } from "react-router-dom";

import {  
    IonLabel, 
    IonButton,  
    IonCol, 
    IonList,
    IonItem,
    IonAvatar,
    IonMenuToggle
  } from '@ionic/react';

import '../assets/css/Custom.css';
import '../assets/css/Responsive.css';

const TENANTS = "tenants"
const RENTS = "rents"
const REPAIRS = "repairs"
const POSTS = "posts"
const STAFF = "staff"

interface Page {
    title: string;
    path: string;
    icon: string;
}

const pages: Page[] = [
    { title: 'Tenants & Leases', path: '/manage-tenants', icon: 'assets/images/Manage-Lease-home.svg' },
    { title: 'Rents', path: '/manage-rents', icon: 'assets/images/Manage-Lease-home.svg' }
];


const DashboardSidebar: React.FC = () => {

    let history = useHistory();

    const [activePage, setActivePage] = useState(pages[0].title);

    const renderMenuItems = (): JSX.Element[] => {
        return pages.map((page: Page) => (
            // <IonMenuToggle key={page.title} auto-hide="false">
                <IonItem>
                    {/* <IonLabel>
                        {page.title}
                    </IonLabel> */}
                    <IonButton className="dashboard-button" color={page.title === activePage ? 'primary' : ''} fill="clear" onClick={() => navigateToPage(page)}>
                        <div className="dashboard-button-inner">
                            <IonAvatar>
                                <img src={page.icon} />
                            </IonAvatar>
                            <IonLabel  className="dashboard-button-label">{page.title}</IonLabel>
                        </div>
                    </IonButton>
                </IonItem>
            // </IonMenuToggle>
        ));
    }

    const navigateToPage = (page: Page) => {
        history.push(page.path);
        setActivePage(page.title);
    }

    return (

        <IonCol className="dashboard-sidebar" size="12" sizeMd="12" sizeLg="3" sizeXl="2">
            <p className="ion-hide-lg-up">What would you like to do today?</p>

            <IonList className="sidebar-menu-list">
                { renderMenuItems() }
                <IonItem>
                    <IonButton className="dashboard-button" fill="clear" routerLink="/manage-tenants">
                        <div className="dashboard-button-inner">
                            <IonAvatar>
                                <img src="assets/images/Manage-Lease-home.svg" />
                            </IonAvatar>
                            <IonLabel  className="dashboard-button-label"><b>Tenants</b> & <b>Leases</b></IonLabel>
                        </div>
                    </IonButton>
                </IonItem>

                <IonItem>
                    <IonButton className="dashboard-button" fill="clear" routerLink="/manage-rents">
                        <div className="dashboard-button-inner">    
                            <IonAvatar>
                                <img src="assets/images/Pay-Rent-home.svg" />
                            </IonAvatar>
                            <IonLabel  className="dashboard-button-label"><b>Rents</b></IonLabel>
                        </div>
                    </IonButton>
                </IonItem>

                <IonItem>
                    <IonButton className="dashboard-button" fill="clear" routerLink="#">
                        <div className="dashboard-button-inner">
                            <IonAvatar>
                                <img src="assets/images/Repair-home.svg" />
                            </IonAvatar>
                            <IonLabel  className="dashboard-button-label"><b>Repairs</b> & <b>Requests</b></IonLabel>
                        </div>
                    </IonButton>
                </IonItem>

                <IonItem>
                    <IonButton className="dashboard-button" fill="clear" routerLink="#">
                        <div className="dashboard-button-inner">
                            <IonAvatar>
                                <img src="assets/images/Posts-Home-Icon.svg" />
                            </IonAvatar>
                            <IonLabel  className="dashboard-button-label"><b>Posts</b> & <b>Marketplace</b></IonLabel>
                        </div>
                    </IonButton>
                </IonItem>

                <IonItem>
                    <IonButton className="dashboard-button" slot="start" fill="clear" routerLink="#">
                        <div className="dashboard-button-inner">
                            <IonAvatar>
                                <img src="assets/images/Staff-home.svg" />
                            </IonAvatar>
                            <IonLabel  className="dashboard-button-label"><b>Staff</b> accesses</IonLabel>
                        </div>
                    </IonButton>
                </IonItem>
            </IonList>
        </IonCol>
        
    );
};

export default DashboardSidebar;
