import React, {useEffect, useRef, useState} from "react";

import { useHistory } from "react-router-dom";

import { 
    IonContent, 
    IonPage,
    IonGrid,
    IonRow,
    IonCol, 
    IonButton, 
    IonIcon,
    IonLabel,
    IonInput,
    IonDatetime,
    IonCard,
    IonCardHeader,
    IonCardTitle,
    IonCardContent,
} from '@ionic/react';

import { attachOutline } from "ionicons/icons";

import { FormProvider, useForm } from "react-hook-form";
import { ErrorMessage } from '@hookform/error-message';

import HeaderMain from '../components/Header-main';
import Footer from '../components/Footer';
import FooterMobile from '../components/Footer-mobile';
import DashboardSidebar from '../components/Dahsboard-sidebar';

import '@ionic/react/css/flex-utils.css';
import '../assets/css/Custom.css';
import '../assets/css/Responsive.css';
import { Routes } from "../App";

const CreatePosts: React.FC<{ path: string }> = ({path}) => {

    let history = useHistory();

    const methods = useForm();
    const { register, handleSubmit, formState: { errors } } = methods;

   const onSubmit = (data: any) => {
      console.log(data);
   };

    const contentRef = useRef<HTMLIonContentElement | null>(null);

    const scrollToBottom= () => {
        contentRef.current && contentRef.current.scrollToBottom(500);
    };

    const [selectedDate, setSelectedDate] = useState<string>('');

    return (
        <IonPage>
  
           <HeaderMain pageTitle="Manage Posts"  logoHide="hide-logo" />
  
           <IonContent className="dashboard-wrapper lease-info-wrapper" ref={contentRef} scrollEvents={true} fullscreen>
                <IonGrid className="dashboard-main-grid">
                    <IonRow className="dashboard-main-row">
                        
                        {/* sidebar start  */}
                        <DashboardSidebar path={path} />
                        {/* sidebar end  */}


                        {/* dashboar content start */}
                        <IonCol className="dashboard-content" size="12" sizeMd="12" sizeLg="6" sizeXl="8">
                            <FormProvider {...methods}>
                            <form onSubmit={handleSubmit(onSubmit)}>
                                <div className="manage-posts-wrapper">
                                    <IonCard className="special-actions-card">
                                        <IonCardHeader>
                                            <IonCardTitle>Post Info</IonCardTitle>
                                        </IonCardHeader>

                                        <IonCardContent>
                                            <div>
                                                <IonLabel className="form-lable">Post Title*</IonLabel>
                                                <IonInput
                                                    mode="md"
                                                    type="text"   
                                                    {...register('postTitle', {
                                                        required: 'This is a required field'
                                                    })}
                                                />
                                                <ErrorMessage
                                                    errors={errors}
                                                    name="email"
                                                    as={<div className="error-message" style={{ color: 'red' }} />}
                                                />
                                            </div>
                                            <div className="upload-photo-block">
                                                <IonButton className="upload-photo-btn" fill="solid" shape="round">
                                                    <IonIcon icon={attachOutline} />
                                                </IonButton>
                                                <IonButton className="take-photo-btn" fill="solid" shape="round">
                                                    <IonIcon icon="assets/images/add-photo.svg" />
                                                </IonButton>
                                            </div>
                                        </IonCardContent>
                                    </IonCard>

                                    <IonCard className="special-actions-card">
                                        <IonCardHeader>
                                            <IonCardTitle>Post Settings</IonCardTitle>
                                        </IonCardHeader>

                                        <IonCardContent>
                                            <div>
                                                <div className="label-with-tooltip">
                                                    <IonLabel className="form-lable">Activation Date</IonLabel>
                                                    <div className="tooltip" title="Add Activation Date">!</div>
                                                </div>
                                                <div className="date-picker width-50">
                                                    <IonDatetime 
                                                        displayFormat="MMM DD, YYYY"
                                                        placeholder="Select Date" 
                                                        value={selectedDate} 
                                                        onIonChange={e => setSelectedDate(e.detail.value!)}
                                                        mode="md"  
                                                        {...register('activationDate', {
                                                            required: 'Please select Activation Date'
                                                        })}
                                                    >
                                                    </IonDatetime>
                                                    <IonIcon icon="assets/images/calendar-icon.svg" />
                                                </div>
                                                <ErrorMessage
                                                    errors={errors}
                                                    name="email"
                                                    as={<div className="error-message" style={{ color: 'red' }} />}
                                                />
                                            </div>

                                            <div>
                                                <div className="label-with-tooltip">
                                                    <IonLabel className="form-lable">Expiration Date</IonLabel>
                                                    <div className="tooltip" title="Add Activation Date">!</div>
                                                </div>
                                                <div className="date-picker width-50">
                                                    <IonDatetime 
                                                        displayFormat="MMM DD, YYYY"
                                                        placeholder="Select Date" 
                                                        value={selectedDate} 
                                                        onIonChange={e => setSelectedDate(e.detail.value!)}
                                                        mode="md"  
                                                        {...register('expirationDate', {
                                                            required: 'Please select Expiration Date'
                                                        })}
                                                    >
                                                    </IonDatetime>
                                                    <IonIcon icon="assets/images/calendar-icon.svg" />
                                                </div>
                                                <ErrorMessage
                                                    errors={errors}
                                                    name="email"
                                                    as={<div className="error-message" style={{ color: 'red' }} />}
                                                />
                                            </div>
                                        </IonCardContent>
                                    </IonCard>
                                </div>
                            </form>
                            </FormProvider>

                        </IonCol>
                        {/* dashboar content end */}


                        {/* dashboar posts start */}
                        <IonCol  className="dashboard-posts" size="12" sizeMd="12" sizeLg="3" sizeXl="2">
                            
                        </IonCol>
                        {/* dashboar post end */}
    
                    </IonRow>
                </IonGrid>

                <IonButton className="scroll-to-bottom-btn" onClick={scrollToBottom} fill="clear">
                    <IonIcon icon="assets/images/double-arrow-down.svg" />
                </IonButton>
              
                <Footer />

            </IonContent>

            <FooterMobile />
      </IonPage>
            
        
        
    );
};

export default CreatePosts;
