import React, { useState } from 'react';

import { 
    IonHeader, 
    IonToolbar,
    IonImg,
    IonButton,
    IonButtons,
    IonBackButton,
    IonPopover,
    IonIcon
} from '@ionic/react';

import '../assets/css/Custom.css';
import '../assets/css/Responsive.css';

const HeaderMain: React.FC = () => {

    const [popoverState, setShowPopover] = useState({ showPopover: false, event: undefined });

    return (
        
        <IonHeader className="inner-main-header">
            <IonToolbar>
                <IonButtons slot="start">
                    <IonBackButton icon="assets/images/back-arrow.svg" />
                </IonButtons>
                <div className="apart-logo">
                    <a href="/Home">
                        <IonImg className="logo" src="assets/images/aparto-logo.svg" />
                    </a>
                </div>

                <IonPopover
                    cssClass='my-custom-class'
                    event={popoverState.event}
                    isOpen={popoverState.showPopover}
                    onDidDismiss={() => setShowPopover({ showPopover: false, event: undefined })}
                >
                    <p>This is popover content</p>
                </IonPopover>
                <IonButton onClick={
                    (e: any) => {
                    e.persist();
                    setShowPopover({ showPopover: true, event: e })
                    }}
                >
                   <IonIcon slot="icon-only" src="/assets/images/google.svg" />
                </IonButton>
            </IonToolbar>
        </IonHeader>
        
    );
};

export default HeaderMain;
