import React, { useEffect, useRef, useState } from 'react';

import {
   GoogleReCaptchaProvider,
   useGoogleReCaptcha
} from 'react-google-recaptcha-v3';

import { 
   IonContent, 
   IonPage, 
   IonGrid, 
   IonRow,
   IonCol, 
   IonLabel, 
   IonInput, 
   IonButton, 
    IonSlides, 
    IonSlide, 
} from '@ionic/react';

import { FormProvider, useForm } from "react-hook-form";
import { ErrorMessage } from '@hookform/error-message';

import Header from '../components/Header';

import '@ionic/react/css/ionic-swiper.css';
import '../assets/css/Custom.css';
import '../assets/css/Responsive.css';

const slideOpts = {
   initialSlide: 0,
   speed: 400
};

const StaffSignup: React.FC = () => {

   const mySlides = useRef<any>(null);

   const methods = useForm();
   const { register, trigger, handleSubmit, getValues, setValue, formState: { errors } } = methods;
   // console.log(errors);

   const { executeRecaptcha } = useGoogleReCaptcha();

   const handleReCaptchaVerify = async () => {
      if (!executeRecaptcha) {
         console.log('Execute recaptcha not yet available');
      }

      // const token = await executeRecaptcha('yourAction');
   };

   useEffect(() => {
      mySlides.current.lockSwipes(true);
      
      handleReCaptchaVerify();
      // }, [handleReCaptchaVerify]);

      // return <button onClick={handleRecaptchaVerify}>Verify recaptcha</button>;

   });

   const next = async (fields: any) => {
      const result = await trigger(fields);
      if (!result) return;
      await mySlides.current.lockSwipes(false);
      await mySlides.current.slideNext();
      await mySlides.current.lockSwipes(true);
   };
   const prev = async () => {
      await mySlides.current.lockSwipes(false);
      await mySlides.current.slidePrev();
      await mySlides.current.lockSwipes(true);
   };
    
   const onSubmit = (data: any) => {
      console.log(data);
   };

   return (
      <IonPage>

         <Header class="with-back-arrow with-step-arrow" onBack={prev} />

         <IonContent fullscreen>
            <IonGrid>
               <IonRow className="signup-form login-form-row">
                  <IonCol size="10" sizeMd="6" sizeLg="4">
                  <FormProvider {...methods}>
                     <form onSubmit={handleSubmit(onSubmit)}>
                        <IonSlides pager={true} options={slideOpts} ref={mySlides}>
                           <IonSlide>
                                 <IonGrid>
                                    <IonRow>
                                       <IonCol size="12" className="email-field">
                                          <IonLabel className="form-lable">Email*</IonLabel>
                                          <IonInput
                                             mode="md"
                                             type="email"
                                             {...register('email', {
                                                required: 'Email is a required field',
                                                pattern: {
                                                   value: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i,
                                                   message: 'invalid email address'
                                                }
                                             })}
                                          />
                                          <ErrorMessage
                                             errors={errors}
                                             name="email"
                                             as={<div className="error-message" style={{ color: 'red' }} />}
                                          />
                                       </IonCol>

                                       <IonCol size="12" className="password-field">
                                          <IonLabel className="form-lable">Password*</IonLabel>
                                          <IonInput 
                                             mode="md"
                                             type="password" 
                                             {...register('password1', {
                                                required: 'Password is Required'
                                             })}
                                          />
                                          <ErrorMessage
                                             errors={errors}
                                             name="password1"
                                             as={<div className="error-message" style={{ color: 'red' }} />}
                                          />
                                       </IonCol>

                                       <IonCol size="12" className="forget-password">
                                          <IonLabel className="form-lable">Confirm Password*</IonLabel>
                                          <IonInput 
                                             mode="md"
                                             type="password" 
                                             {...register('password2', {
                                                // required: true,
                                                validate: {
                                                   noMatch: (value: string) => {
                                                      return value !== getValues("password1")
                                                         ? "Passwords do not match"
                                                         : undefined;
                                                   },
                                                },
                                             })}
                                          />
                                          <ErrorMessage
                                             errors={errors}
                                             name="password2"
                                             as={<div className="error-message" style={{ color: 'red' }} />}
                                          />
                                       </IonCol>

                                       <IonCol size="12" className="sign-up-btn">
                                          <IonButton expand="block" shape="round" fill="outline" onClick={() => next(['email','password1', 'password2'])}>
                                             Continue
                                          </IonButton>
                                       </IonCol>

                                    </IonRow>
                                 </IonGrid>
                           </IonSlide>

                           <IonSlide>
                                 <IonGrid>
                                    <IonRow>

                                       <IonCol size="12" className="email-field">
                                          <IonLabel className="form-lable">First Name*</IonLabel>
                                          <IonInput
                                             mode="md"
                                             type="text"   
                                             {...register('firstName', {
                                                required: 'First name is a required field'
                                             })}
                                          />
                                          <ErrorMessage
                                             errors={errors}
                                             name="firstName"
                                             as={<div className="error-message" style={{ color: 'red' }} />}
                                          />
                                       </IonCol>

                                       <IonCol size="12" className="email-field">
                                          <IonLabel className="form-lable">Last Name*</IonLabel>
                                          <IonInput
                                             mode="md"
                                             type="text" 
                                             {...register('lastName', {
                                                required: 'Last name is a required field'
                                             })}
                                          />
                                          <ErrorMessage
                                             errors={errors}
                                             name="lastName"
                                             as={<div className="error-message" style={{ color: 'red' }} />}
                                          />
                                       </IonCol>

                                       <IonCol size="12" className="email-field">
                                          <IonLabel className="form-lable">Telephone:</IonLabel>
                                          <IonInput
                                             mode="md"
                                             type="number" 
                                             {...register('telephone', {
                                                minLength: 6, maxLength: 12,
                                             })}
                                          />
                                          <ErrorMessage
                                             errors={errors}
                                             name="telephone"
                                             as={<div className="error-message" style={{ color: 'red' }} />}
                                          />
                                       </IonCol>

                                       {/* <IonCol size="12" className="sign-up-btn">
                                          <IonButton expand="block" shape="round" fill="outline" onClick={() => prev()}>
                                             previous
                                          </IonButton>
                                       </IonCol> */}

                                       <IonCol size="12" className="sign-up-btn">
                                          <GoogleReCaptchaProvider reCaptchaKey="6LfL2pMcAAAAAJGqw3rePggWbAJtKARjHPlf-ax2">
                                             {/* <YourReCaptchaComponent /> */}
                                          </GoogleReCaptchaProvider>
                                          </IonCol>

                                       <IonCol size="12" className="sign-up-btn">
                                          <IonButton type="submit" expand="block" shape="round" fill="outline">
                                             Finalize
                                          </IonButton>
                                       </IonCol>

                                    </IonRow>
                                 </IonGrid>
                           </IonSlide>

                        </IonSlides>
                     </form>
                  </FormProvider>
                  </IonCol>

               </IonRow>
            </IonGrid>
         </IonContent>

      </IonPage>
   );
};

export default StaffSignup;
